INSERT INTO `price` (`id`, `name`, `price`, `selloff`, `hits`, `special`, `status`, `summary`) VALUES
(106, 'CHƯƠNG TRÌNH TOUR TẾT ÂM LỊCH 2013 ĐÀ NẴNG – HỘI AN – SƠN TRÀ - BÀ NÀ  ', 3560000, 0, 87, 0, 1, 'Buổi sáng: Quý khách có mặt tại sân bay Tân Sơn Nhất (ga quốc nội) 90 phút trước giờ bay, làm thủ tục bay đi Đà Nẵng. Xe và HDV đón khách tại sân bay Đà Nẵng , Quý khách khởi hành đi Hội An . Ăn trưa nhà hàng tại Hội An.'),
(107, 'ĐÀ LẠT - THÀNH PHỐ NGÀN HOA', 1695000, 0, 42, 1, 1, 'Khi đặt chân lên Đà Lạt, nằm trên cao nguyên Langbiang, Quý khách sẽ không khỏi ngỡ ngàng trước cảnh sắc thiên nhiên, thác đổ ào ào, suối tuôn róc rách và đặc biệt là các loài hoa. Không nơi nào trên đất nước ta lại có nhiều hoa như Đà Lạt: từ hoa rừng nhiệt đới tới các loài hoa của Phương Đông, Phương Tây. Chính những điều này đã đem đến cho Đà Lạt sức hấp dẫn diệu kì đối với du khách trong và ngòai nước ...'),
(108, 'Tour Du lịch Úc', 57500000, 0, 48, 0, 1, 'Australia  là quốc gia độc nhất trên thế giới chiếm ngự cả một lục địa với diện tích 7 741 220 km2 nằm ở phía Nam đường xích đạo giữa Ấn độ dương và Thái Bình Dương.  Australia là đất nước xinh đẹp với những vùng sa mạc rộng lớn, với những ngôi nhà cùng những khu vườn đầy hoa rực rỡ, với những thành phố hiện đại, và những bãi biển nổi tiếng thế giới. Australia  là nơi mà bạn có thể nhìn thấy những loài thú dễ thương như từng đàn Kangaroo nhảy nhót, xuyên qua những bãi cỏ dọc theo đường đến trường hoặc nghe những chú sóc Possum chạy ríu rít trên mái nhà.'),
(109, 'LỊCH KHỞI HÀNH TẾT ÂM LỊCH 2013', 0, 0, 67, 0, 1, 'Du xuân dịp Tết không còn quá xa lạ với du khách Việt trong những năm gần đây. Năm nay, với lịch nghỉ Tết Nguyên Đán 2013 kéo dài 10 ngày, Du lịch Sắc Màu cho ra mắt chùm tour du xuân thú vị, tạo cơ hội cho du khách thoải mái lựa chọn những hành trình mới, lạ và hấp dẫn'),
(110, 'NHA TRANG - MIỀN CÁT TRẮNG', 1850000, 0, 93, 0, 1, 'Đến Nha Trang để thưởng ngoạn phong cảnh hiền hòa của miền quê cát trắng và thưởng thức những món ăn đậm chất miền biển...'),
(112, 'Malaysia - Thiên đường mua sắm', 12639000, 0, 27, 0, 1, 'Đất nước của tòa tháp đôi nổi tiếng\r\nĐất nước Malaysia không chỉ đẹp bởi sự cổ kính của các công trình kiến trúc mà còn hào nhoáng bởi phong cách hiện đại, đây sự kết hợp hoàn hảo giữa một Malaysia huyền bí cổ kính pha bời nét kiến trúc độc đáo và quần thể nhiều hòn đảo xinh đẹp, hoang sơ như Langkawi, Penang, Malacca… '),
(113, 'Tham quan đất nước triệu voi', 5700000, 0, 19, 0, 1, 'Được mệnh danh là Miền đất Triệu Voi- Lào nằm ở nơi giao hội của hai nền văn minh vĩ đại và hùng mạnh nhất châu Á là Ấn Độ và Trung Hoa, người dân Lào đã hấp thụ những phong tục và tín ngưỡng của hai nền văn minh ấy để hình thành nên một nền văn hóa đặc sắc của riêng mình hết sức độc đáo. '),
(114, 'Nơi bắt đầu Bản tình ca mùa đông', 20900000, 0, 42, 0, 1, 'Đến Hàn Quốc, ngoài những danh lam thắng cảnh nổi tiếng tại Seoul, du khách sẽ được tới đảo Cheju- điểm hẹn tình yêu. Nếu như đảo Nami làm du khách mê hồn trước những khung cảnh mộng mơ trữ tình đã từng được xem trên phim truyền hình Hàn Quốc thì đảo Cheju hiện ra diễm lệ xứng danh “thiên đường du lịch” với nhiều bãi biển, cánh đồng xanh ngát, không khí trong lành...'),
(111, 'Đảo thiên đường Bali', 16400000, 0, 104, 1, 1, 'Bali quả đúng là thiên đường du lịch. Thời tiết mát mẻ càng khiến người ta muốn khám phá mọi vẻ đẹp trong từng ngõ ngách của Bali: bãi biển dài xanh thẳm, những ngọn núi lửa hùng vĩ, hàng vạn đền thờ cổ kính và những ngôi nhà cổ được trang trí muôn hình dáng nghộ nghĩnh, xinh xắn nằm xuôi theo các con phố nhỏ kiến trúc độc đáo. Đặc biệt, du khách sẽ được tới tham quan đảo Rùa – khu bảo tồn nhiều loài Rùa quý hiếm còn lại trên thế giới và giống rùa biển đặc trưng của Bali, tham quan công viên Garuda Wishnu Park - xem toàn cảnh phía nam đảo Bali, đền thiêng Tanah Lot – nơi thực hiện các nghi lễ bày tỏ lòng kính trọng của con người đối với sự che chở của biển cả với hình tượng những con rắn biển được truyền tụng là kẻ bảo vệ ngôi đền chống những thể lực xấu hoặc kẻ xâm nhập'),
(115, 'Nha trang 4 ngày 3 đêm', 0, 0, 36, 1, 1, 'Đoàn đi tàu tham quan vịnh Nha Trang_một trong 29 vịnh biển đẹp nhất thế giới, du khách ngắm cảnh làng chài, Bãi Sạn, Hòn Tằm, Hòn Tre... đoàn tiếp tục đến với khu vui chơi giải trí 4 sao Diamond Bay tắm biển và chơi các trò chơi trên biển như: Môtô nước, dù  bay, xuồng thể thao, lướt ván, thuyền chuối tại bãi tắm Nhũ Tiên. Tham quan khu vui chơi giải trí Wonder land (Sông Lô), nơi diễn ra cuộc thi hoa hậu Hoàn Vũ 2008.'),
(116, 'Phú Quốc - Huyền Thoại đảo ngọc', 0, 0, 14, 0, 1, 'Phú Quốc(Kiên Giang) được mệnh danh là hòn đảo ngọc của Việt Nam với những bãi các trắng trãi dài dọc theo bờ biển với những gặng san hô với lòng biển sâu nơi sinh sống của rất nhiều loại cá đủ các màu sắc rất đẹp. Đến Phú Quốc ngoài việc bạn được ngấm nhìn những cảnh đẹp tuyệt vời mà bạn còn có cơ hội thưởng thức hải sản tươi sống được đánh bắt từ hòn đảo xinh đẹp này được chế biến rất công phu từ bàn tay của ngư phủ nơi đây.Phú Quốc là nơi lý tưởng cho bạn trong những dịp thư giãn, nghĩ ngơi cùng gia đình và bạn bè!\r\nPhú Quốc thân yêu chào đón quý khách!\r\n'),
(117, 'Chiêm ngưỡng kinh đô điện ảnh thế giới', 77900000, 0, 20, 0, 1, 'Du Lịch Hoa Kỳ là thiên đường du lịch của thế giới, là điểm đến yêu thích của hầu hết mọi người bởi nơi đây có nhiều phong cảnh đẹp ,những trung tâm mua sắm sầm uất, đáp ứng được mọi thị hiếu của du khách...\r\n'),
(118, 'Một thoáng bờ Tây Hoa kỳ', 62900000, 0, 31, 0, 1, 'Đoàn tập trung tại Sân bay Quốc tế Tân Sơn Nhất Nội Bài. Hướng dẫn viên đón và hướng dẫn Quý khách làm thủ tục check-in. Đoàn đáp chuyến bay đi Taipei. Đến Taipei, Đoàn tiếp tục nối chuyến bay đi Los Angeles...'),
(119, 'Du lịch bờ đông Hoa Kỳ', 63800000, 0, 27, 1, 1, 'Đoàn tập trung tại Sân bay Quốc tế Tân Sơn Nhất/ Nội Bài.\r\nHướng dẫn viên đón và hướng dẫn Quý khách làm thủ tục check-in. Đoàn đáp chuyến bay đi Taipei. Đến Taipei, Đoàn tiếp tục nối chuyến bay đi New York.\r\nNghỉ ngơi trên máy bay');
